(18, 'Nelson\'s Column', 'nelson', ''),
(19, 'Dennis Severs’ House', 'dennis,dennis severs house,sever,Spitalfields,house,dennis servers,Huguenot weavers,Dennis Severs\' House,history of Huguenot weavers,history of dennis servers,history of dennis servers\' house', ''),
